 22: function ThresholdBar({ actual, threshold, direction }: {
 23:   actual: number;
 24:   threshold: number;
 25:   direction: 'above' | 'below';
 26: }) {
 27:   const max = Math.max(actual, threshold) * 1.2 || 1;
 28:   const actualPct = Math.max(0, Math.min((actual / max) * 100, 100));
 29:   const thresholdPct = Math.max(0, Math.min((threshold / max) * 100, 100));
 30:   const isViolating = direction === 'below' ? actual < threshold : actual > threshold;
 31: 
 32:   return (
 33:     <div className="threshold-bar" aria-label={`Actual: ${actual.toFixed(4)}, Threshold: ${threshold.toFixed(4)}`}>
 34:       <div
 35:         className="threshold-bar-fill"
 36:         style={{
 37:           width: `${actualPct}%`,
 38:           background: isViolating ? 'var(--status-critical)' : 'var(--status-healthy)',
 39:         }}
 40:       />
 41:       <div
 42:         className="threshold-bar-marker"
 43:         style={{ left: `${thresholdPct}%` }}
 44:         title={`Threshold: ${threshold.toFixed(4)}`}
 45:       />
 46:     </div>
 47:   );
 48: }
 49: 
 50: function CompoundAlertCard({ alert }: { alert: AlertWithMeta }) {
 51:   return (
 52:     <li className={`alert-item alert-compound ${alert.severity}`}>
 53:       <div className="alert-message">{alert.message}</div>
 54:       <div className="alert-meta">
 55:         {alert.aggregation} = {alert.actualValue?.toFixed(4)} (threshold: {alert.threshold?.toFixed(4)})
 56:       </div>
 57: 
 58:       <ThresholdBar
 59:         actual={alert.actualValue}
 60:         threshold={alert.threshold}
 61:         direction={alert.direction}
 62:       />
 63: 
 64:       {alert.remediationHints && alert.remediationHints.length > 0 && (
 65:         <ol className="remediation-list">
 66:           {alert.remediationHints.map((hint, i) => (
 67:             <li key={i}>{hint}</li>
 68:           ))}
 69:         </ol>
 70:       )}
 71: 
 72:       {alert.relatedMetrics && alert.relatedMetrics.length > 0 && (
 73:         <div className="alert-related">
 74:           <span style={{ fontSize: 12, color: 'var(--text-secondary)' }}>Related:</span>
 75:           {alert.relatedMetrics.map((m) => (
 76:             <Link key={m} href={`/metrics/${m}`} className="alert-metric-link">
 77:               {m}
 78:             </Link>
 79:           ))}
 80:         </div>
 81:       )}
 82:     </li>
 83:   );
 84: }
