 86: export function AlertList({ alerts }: { alerts: AlertWithMeta[] }) {
 87:   if (alerts.length === 0) return null;
 88: 
 89:   const sorted = [...alerts].sort((a, b) => {
 90:     const order = { critical: 0, warning: 1, info: 2 };
 91:     return (order[a.severity as keyof typeof order] ?? 2) - (order[b.severity as keyof typeof order] ?? 2);
 92:   });
 93: 
 94:   return (
 95:     <ul className="alert-list">
 96:       {sorted.map((alert, i) =>
 97:         alert.isCompound
 98:           ? <CompoundAlertCard key={`${alert.message}-${i}`} alert={alert} />
 99:           : <SimpleAlertItem key={`${alert.message}-${i}`} alert={alert} />
100:       )}
101:     </ul>
102:   );
