19: export function TrendIndicator({ trend }: { trend?: MetricTrend }) {
20:   if (!trend) return null;
21:   const arrow = trend.direction === 'improving' ? '\u2191' : trend.direction === 'degrading' ? '\u2193' : '\u2192';
22:   const pct = trend.percentChange ?? 0;
23:   const sign = pct > 0 ? '+' : '';
24:   return (
25:     <span className={`trend ${trend.direction}`} aria-label={`Trend: ${trend.direction}, ${sign}${pct.toFixed(1)}%`}>
26:       {arrow} {sign}{pct.toFixed(1)}%
27:       {trend.lowSampleWarning && <span title="Low sample count"> *</span>}
28:     </span>
29:   );
30: }
