10: function PipelineFunnelInner({ stages, dropoffs, overallConversionPercent }: PipelineFunnelProps) {
11:   if (stages.length === 0) return <p>No pipeline data available.</p>;
12: 
13:   const maxCount = Math.max(...stages.map(s => s.entryCount), 1);
14: 
15:   return (
16:     <div role="region" aria-label="Evaluation pipeline funnel">
17:       <div style={{ display: 'flex', alignItems: 'center', gap: 8, marginBottom: 16 }}>
18:         <span style={{ fontFamily: 'var(--font-mono)', fontSize: 20, fontWeight: 600 }}>
19:           {overallConversionPercent.toFixed(1)}%
20:         </span>
21:         <span style={{ color: 'var(--text-secondary)', fontSize: 14 }}>overall conversion</span>
22:       </div>
23: 
24:       <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
25:         {stages.map((stage, idx) => {
26:           const widthPct = Math.max(2, (stage.entryCount / maxCount) * 100);
27:           const dropoff = dropoffs[idx];
28:           const showDropoff = dropoff && dropoff.dropped > 0;
29: 
30:           return (
31:             <div key={stage.name}>
32:               {/* Stage bar */}
33:               <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
34:                 <div
35:                   style={{
36:                     width: `${widthPct}%`,
37:                     minWidth: 40,
38:                     height: 32,
39:                     background: `var(--stage-${stage.name}, var(--accent))`,
40:                     borderRadius: 4,
41:                     display: 'flex',
42:                     alignItems: 'center',
43:                     justifyContent: 'space-between',
44:                     padding: '0 10px',
45:                     transition: 'width 0.3s ease',
46:                   }}
47:                   role="meter"
48:                   aria-label={`${stage.displayName}: ${stage.entryCount} evaluations`}
49:                   aria-valuenow={stage.entryCount}
50:                   aria-valuemin={0}
51:                   aria-valuemax={maxCount}
52:                 >
53:                   <span style={{ fontSize: 12, fontWeight: 600, color: '#fff', whiteSpace: 'nowrap' }}>
54:                     {stage.displayName}
55:                   </span>
56:                   <span style={{ fontSize: 12, fontFamily: 'var(--font-mono)', color: '#fff', whiteSpace: 'nowrap' }}>
57:                     {stage.entryCount.toLocaleString()}
58:                   </span>
59:                 </div>
60:               </div>
61: 
62:               {/* Drop-off indicator */}
63:               {showDropoff && (
64:                 <div style={{
65:                   fontSize: 11,
66:                   color: dropoff.dropoffPercent > 20 ? 'var(--status-warning)' : 'var(--text-secondary)',
67:                   paddingLeft: 10,
68:                   margin: '2px 0',
69:                 }}>
70:                   {'\u2193'} -{dropoff.dropped.toLocaleString()} ({dropoff.dropoffPercent.toFixed(1)}% drop)
71:                 </div>
72:               )}
73:             </div>
74:           );
75:         })}
76:       </div>
77: 
78:       {/* Screen reader summary table */}
79:       <table className="sr-only" aria-label="Pipeline stage details">
80:         <thead>
81:           <tr><th>Stage</th><th>Entry</th><th>Exit</th><th>Drop-off</th></tr>
82:         </thead>
83:         <tbody>
84:           {stages.map((stage, idx) => (
85:             <tr key={stage.name}>
86:               <td>{stage.displayName}</td>
87:               <td>{stage.entryCount}</td>
88:               <td>{stage.exitCount}</td>
89:               <td>{dropoffs[idx] != null ? `${dropoffs[idx].dropoffPercent.toFixed(1)}%` : '\u2014'}</td>
90:             </tr>
91:           ))}
92:         </tbody>
93:       </table>
94:     </div>
95:   );
96: }
