 2: 
 3: interface SparklineProps {
 4:   /** Array of score values (nulls allowed for gaps) */
 5:   data: (number | null)[];
 6:   /** SVG width in px */
 7:   width?: number;
 8:   /** SVG height in px */
 9:   height?: number;
10:   /** Stroke color (CSS var or hex) */
11:   color?: string;
12:   /** Accessible label for the sparkline */
13:   label?: string;
14: }
15: 
16: function SparklineInner({ data, width = 80, height = 24, color = 'var(--text-secondary)', label }: SparklineProps) {
17:   const values = data.filter((v): v is number => v !== null && Number.isFinite(v));
18:   if (values.length < 2) return null;
19: 
20:   const min = Math.min(...values);
21:   const max = Math.max(...values);
22:   const range = max - min || 1;
23:   const pad = 2;
24:   const xDenom = data.length > 1 ? data.length - 1 : 1;
25: 
26:   const points = data
27:     .map((v, i) => {
28:       if (v === null || !Number.isFinite(v)) return null;
29:       const x = pad + (i / xDenom) * (width - pad * 2);
30:       const y = pad + (1 - (v - min) / range) * (height - pad * 2);
31:       return `${x},${y}`;
32:     })
33:     .filter((p): p is string => p !== null)
34:     .join(' ');
35: 
36:   return (
37:     <svg
38:       width={width}
39:       height={height}
40:       viewBox={`0 0 ${width} ${height}`}
41:       role="img"
42:       aria-label={label ?? 'Score trend sparkline'}
43:       style={{ display: 'block' }}
44:     >
45:       <polyline
46:         points={points}
47:         fill="none"
48:         stroke={color}
49:         strokeWidth={1.5}
50:         strokeLinecap="round"
51:         strokeLinejoin="round"
52:       />
53:     </svg>
54:   );
55: }
56: 
