 16: function levelColor(level: string): string {
 17:   if (level === 'high') return SCORE_COLORS.excellent;
 18:   if (level === 'medium') return SCORE_COLORS.adequate;
 19:   return SCORE_COLORS.failing;
 20: }
 21: 
 22: function levelShape(level: string): string {
 23:   if (level === 'high') return '\u25CF'; // ●
 24:   if (level === 'medium') return '\u25D0'; // ◐
 25:   return '\u25CB'; // ○
 26: }
