 28: function VarianceBar({ value, max }: { value: number; max: number }) {
 29:   const pct = max > 0 ? Math.min(100, (value / max) * 100) : 0;
 30:   const band: ScoreColorBand = pct < 20 ? 'excellent' : pct < 50 ? 'adequate' : 'failing';
 31:   return (
 32:     <div className="variance-bar">
 33:       <div className="variance-bar-track">
 34:         <div className="variance-bar-fill" style={{ width: `${pct}%`, background: SCORE_COLORS[band] }} />
 35:       </div>
 36:       <span style={{ fontFamily: 'var(--font-mono)', fontSize: 11, color: 'var(--text-secondary)', minWidth: 36 }}>
 37:         {value.toFixed(3)}
 38:       </span>
 39:     </div>
 40:   );
 41: }
 42: 
 43: export function ConfidencePanel({ confidence, evaluatorScores }: ConfidencePanelProps) {
 44:   const { level, sampleCount, scoreStdDev, evaluatorCount, evaluatorAgreement } = confidence;
 45:   const method = evaluatorCount > 1 ? 'multi-judge agreement' : sampleCount > 50 ? 'sample size' : 'sample count';
 46: 
 47:   const hasMultiJudge = evaluatorScores && evaluatorScores.length > 1;
 48: 
 49:   return (
 50:     <div className="confidence-panel">
 51:       <div className="confidence-header">
 52:         <span style={{ color: levelColor(level), fontSize: 14 }}>
 53:           {levelShape(level)} {level}
 54:         </span>
 55:         <span className="confidence-method">({method})</span>
 56:       </div>
 57: 
 58:       <div style={{ display: 'grid', gridTemplateColumns: 'auto 1fr', gap: '4px 16px', fontSize: 12, marginBottom: 12 }}>
 59:         <span style={{ color: 'var(--text-secondary)' }}>Sample Count</span>
 60:         <span style={{ fontFamily: 'var(--font-mono)' }}>{sampleCount}</span>
 61: 
 62:         {scoreStdDev != null && (
 63:           <>
 64:             <span style={{ color: 'var(--text-secondary)' }}>Score Variance</span>
 65:             <VarianceBar value={scoreStdDev} max={0.5} />
 66:           </>
 67:         )}
 68: 
 69:         <span style={{ color: 'var(--text-secondary)' }}>Evaluators</span>
 70:         <span style={{ fontFamily: 'var(--font-mono)' }}>{evaluatorCount}</span>
 71: 
 72:         {evaluatorAgreement != null && (
 73:           <>
 74:             <span style={{ color: 'var(--text-secondary)' }}>Agreement</span>
 75:             <span style={{
 76:               fontFamily: 'var(--font-mono)',
 77:               color: evaluatorAgreement > 0.8 ? SCORE_COLORS.excellent : evaluatorAgreement > 0.5 ? SCORE_COLORS.adequate : SCORE_COLORS.failing,
 78:             }}>
 79:               {(evaluatorAgreement * 100).toFixed(0)}%
 80:             </span>
 81:           </>
 82:         )}
 83:       </div>
 84: 
 85:       {hasMultiJudge && (
 86:         <div>
 87:           <div className="section-label" style={{ marginBottom: 6 }}>Judge Panel</div>
 88:           <div className="agreement-grid">
 89:             <span className="ag-header">Evaluator</span>
