  3: interface SpanNode {
  4:   spanId: string;
  5:   name: string;
  6:   durationMs?: number;
  7:   status?: { code: number; message?: string };
  8:   attributes?: Record<string, unknown>;
  9:   children: SpanNode[];
 10:   evalCount: number;
 11: }
 12: 
 13: interface SpanTreeProps {
 14:   spans: Array<{
 15:     spanId: string;
 16:     name: string;
 17:     durationMs?: number;
 18:     status?: { code: number; message?: string };
 19:     attributes?: Record<string, unknown>;
 20:     parentSpanId?: string;
 21:   }>;
 22:   evalsBySpan: Map<string, number>;
 23:   maxDuration: number;
 24: }
 25: 
 26: function buildTree(
 27:   spans: SpanTreeProps['spans'],
 28:   evalsBySpan: Map<string, number>,
 29: ): SpanNode[] {
 30:   const nodeMap = new Map<string, SpanNode>();
 31:   const roots: SpanNode[] = [];
 32: 
 33:   for (const s of spans) {
 34:     nodeMap.set(s.spanId, {
 35:       spanId: s.spanId,
 36:       name: s.name,
 37:       durationMs: s.durationMs,
 38:       status: s.status,
 39:       attributes: s.attributes,
 40:       children: [],
 41:       evalCount: evalsBySpan.get(s.spanId) ?? 0,
 42:     });
 43:   }
 44: 
 45:   for (const s of spans) {
 46:     const node = nodeMap.get(s.spanId)!;
 47:     const parentId = (s as { parentSpanId?: string }).parentSpanId;
 48:     if (parentId && nodeMap.has(parentId)) {
 49:       nodeMap.get(parentId)!.children.push(node);
 50:     } else {
 51:       roots.push(node);
 52:     }
 53:   }
 54: 
 55:   return roots;
 56: }
 57: 
 58: const STATUS_ICONS: Record<number, string> = {
 59:   0: '',      // UNSET
 60:   1: '\u2713', // OK ✓
 61:   2: '\u2717', // ERROR ✗
 62: };
 63: 
 64: function SpanRow({ node, depth, maxDuration }: { node: SpanNode; depth: number; maxDuration: number }) {
 65:   const barPct = maxDuration > 0 && node.durationMs ? Math.min((node.durationMs / maxDuration) * 100, 100) : 0;
 66:   const statusCode = node.status?.code ?? 0;
 67:   const isError = statusCode === 2;
 68: 
 69:   const barStyle: CSSProperties = {
 70:     height: 4,
 71:     width: `${barPct}%`,
 72:     background: isError ? 'var(--status-critical)' : 'var(--status-healthy)',
 73:     borderRadius: 2,
 74:     marginTop: 4,
 75:     minWidth: barPct > 0 ? 2 : 0,
 76:   };
 77: 
 78:   return (
 79:     <>
 80:       <div
 81:         style={{
 82:           paddingLeft: depth * 20 + 8,
 83:           paddingTop: 8,
 84:           paddingBottom: 8,
 85:           borderBottom: '1px solid var(--border)',
 86:           display: 'flex',
 87:           alignItems: 'center',
 88:           gap: 8,
 89:         }}
 90:       >
 91:         <span style={{ fontSize: 14, color: isError ? 'var(--status-critical)' : 'var(--text-primary)' }}>
 92:           {STATUS_ICONS[statusCode]}
 93:         </span>
 94:         <div style={{ flex: 1 }}>
 95:           <div style={{ fontSize: 13, fontWeight: 500 }}>{node.name}</div>
 96:           <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
 97:             <div style={{ flex: 1, background: 'var(--bg-secondary)', borderRadius: 2, height: 4 }}>
 98:               <div style={barStyle} />
 99:             </div>
100:             {node.durationMs != null && (
101:               <span style={{ fontSize: 11, fontFamily: 'var(--font-mono)', color: 'var(--text-secondary)', whiteSpace: 'nowrap' }}>
102:                 {node.durationMs < 1000 ? `${node.durationMs.toFixed(0)}ms` : `${(node.durationMs / 1000).toFixed(2)}s`}
103:               </span>
