 54: 
 55:   // Build data points: previous and current
 56:   const data: Array<{ label: string; value: number; projected?: number }> = [
 57:     { label: 'Previous', value: trend.previousValue },
 58:     { label: 'Current', value: trend.currentValue },
 59:   ];
 60: 
 61:   // If dynamics has projectedBreachTime or velocity, add projection point
 62:   if (dynamics && dynamics.velocity !== 0) {
 63:     const projectedValue = trend.currentValue + dynamics.velocity;
 64:     data.push({
 65:       label: 'Projected',
 66:       value: trend.currentValue, // actual line ends here
 67:       projected: projectedValue,
 68:     });
 69:     // Set current point's projected value to bridge the dashed line
 70:     data[1].projected = trend.currentValue;
 71:   }
 72: 
 73:   // Compute Y domain with some padding
 74:   const allValues = data.flatMap((d) => [d.value, d.projected].filter((v): v is number => v != null));
 75:   if (warningThreshold != null) allValues.push(warningThreshold);
 76:   if (criticalThreshold != null) allValues.push(criticalThreshold);
 77:   if (allValues.length === 0) {
 78:     return <div style={{ color: COLORS.text, padding: 16, textAlign: 'center' }}>Insufficient data</div>;
 79:   }
 80:   const yMin = Math.min(...allValues);
 81:   const yMax = Math.max(...allValues);
 82:   const yPad = (yMax - yMin) * 0.15 || 0.05;
 83: 
 84:   return (
 85:     <div>
 86:       <div aria-label={`Trend chart for ${metricName}`}>
 87:         <ResponsiveContainer width="100%" height={200}>
 88:           <LineChart data={data} margin={{ top: 8, right: 16, bottom: 4, left: 16 }}>
 89:             <CartesianGrid stroke={COLORS.grid} strokeDasharray="3 3" />
 90:             <XAxis
 91:               dataKey="label"
 92:               tick={{ fill: COLORS.text, fontSize: 12 }}
 93:               stroke={COLORS.grid}
 94:             />
 95:             <YAxis
 96:               domain={[yMin - yPad, yMax + yPad]}
 97:               tick={{ fill: COLORS.text, fontSize: 11 }}
 98:               stroke={COLORS.grid}
 99:               tickFormatter={(v: number) => v.toFixed(2)}
100:               width={48}
101:             />
102:             <Tooltip
103:               contentStyle={{
104:                 backgroundColor: COLORS.tooltip,
105:                 border: `1px solid ${COLORS.grid}`,
106:                 borderRadius: 6,
107:                 color: COLORS.text,
108:                 fontSize: 12,
109:               }}
110:               formatter={(v: number | undefined) => [v != null ? formatValue(v) : 'N/A', '']}
111:               labelStyle={{ color: '#e6edf3' }}
112:             />
113:             {warningThreshold != null && (
114:               <ReferenceLine
115:                 y={warningThreshold}
116:                 stroke={COLORS.warning}
117:                 strokeDasharray="6 3"
118:                 label={{ value: 'Warning', fill: COLORS.warning, fontSize: 11, position: 'right' }}
119:               />
120:             )}
121:             {criticalThreshold != null && (
122:               <ReferenceLine
123:                 y={criticalThreshold}
124:                 stroke={COLORS.critical}
125:                 strokeDasharray="6 3"
126:                 label={{ value: 'Critical', fill: COLORS.critical, fontSize: 11, position: 'right' }}
127:               />
128:             )}
129:             <Line
130:               type="monotone"
131:               dataKey="value"
132:               stroke={COLORS.line}
133:               strokeWidth={2}
134:               dot={{ fill: COLORS.line, r: 4 }}
135:               activeDot={{ r: 6 }}
136:               connectNulls
137:             />
138:             {dynamics && dynamics.velocity !== 0 && (
139:               <Line
140:                 type="monotone"
141:                 dataKey="projected"
142:                 stroke={COLORS.projection}
143:                 strokeWidth={2}
144:                 strokeDasharray="6 4"
145:                 dot={{ fill: COLORS.projection, r: 3, strokeDasharray: '' }}
146:                 connectNulls
147:               />
148:             )}
149:           </LineChart>
150:         </ResponsiveContainer>
151:       </div>
152:       {dynamics && (
153:         <div style={{
154:           display: 'flex',
155:           gap: 24,
156:           flexWrap: 'wrap',
157:           marginTop: 8,
158:           padding: '8px 0',
159:           fontSize: 13,
160:           color: COLORS.text,
161:         }}>
162:           <div>
163:             <span style={{ fontWeight: 600 }}>Velocity:</span>{' '}
164:             <span style={{ fontFamily: 'var(--font-mono)' }}>
165:               {dynamics.velocity >= 0 ? '+' : ''}{formatValue(dynamics.velocity)}/hr
166:             </span>
167:           </div>
168:           <div>
169:             <span style={{ fontWeight: 600 }}>Acceleration:</span>{' '}
170:             <span style={{ fontFamily: 'var(--font-mono)' }}>
171:               {dynamics.acceleration >= 0 ? '+' : ''}{formatValue(dynamics.acceleration)}/hr
172:             </span>
173:           </div>
174:           {dynamics.projectedBreachTime && (
175:             <div>
176:               <span style={{ fontWeight: 600 }}>Breach in:</span>{' '}
177:               <span style={{
178:                 fontFamily: 'var(--font-mono)',
179:                 color: dynamics.projectedStatus === 'critical' ? COLORS.critical : COLORS.warning,
180:               }}>
181:                 {formatBreachTime(dynamics.projectedBreachTime)}
182:               </span>
183:             </div>
184:           )}
185:           <div>
186:             <span style={{ fontWeight: 600 }}>Confidence:</span>{' '}
187:             <span style={{ fontFamily: 'var(--font-mono)' }}>
188:               {(dynamics.confidence * 100).toFixed(0)}%
189:             </span>
190:           </div>
191:         </div>
192:       )}
193:     </div>
194:   );
