 14: interface TrendSeriesProps {
 15:   data: TrendBucket[];
 16:   metricName: string;
 17: }
 18: 
 19: const COLORS = {
 20:   ...CHART_COLORS,
 21:   band: 'rgba(88, 166, 255, 0.1)',
 22:   bandStroke: 'rgba(88, 166, 255, 0.3)',
 23:   background: '#0d1117',
 24: };
 25: 
 26: function formatTime(iso: string, spanDays: number): string {
 27:   const d = new Date(iso);
 28:   if (spanDays <= 1) return d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
 29:   if (spanDays <= 7) return d.toLocaleDateString([], { weekday: 'short', hour: '2-digit' });
 30:   return d.toLocaleDateString([], { month: 'short', day: 'numeric' });
 31: }
 32: 
 33: export function TrendSeries({ data, metricName }: TrendSeriesProps) {
 34:   if (data.length === 0) {
 35:     return (
 36:       <div style={{ color: COLORS.text, padding: 16, textAlign: 'center' }}>
 37:         No trend data available
 38:       </div>
 39:     );
 40:   }
 41: 
 42:   const spanMs = new Date(data[data.length - 1].endTime).getTime() - new Date(data[0].startTime).getTime();
 43:   const spanDays = spanMs / (1000 * 60 * 60 * 24);
 44: 
 45:   const chartData = data.map((bucket) => ({
 46:     time: formatTime(bucket.startTime, spanDays),
 47:     avg: bucket.avg,
 48:     p10: bucket.percentiles?.p10 ?? null,
 49:     p50: bucket.percentiles?.p50 ?? null,
 50:     p90: bucket.percentiles?.p90 ?? null,
 51:     count: bucket.count,
 52:   }));
 53: 
 54:   const allVals = chartData.flatMap(d =>
 55:     [d.avg, d.p10, d.p90].filter((v): v is number => v !== null)
 56:   );
 57:   if (allVals.length === 0) {
 58:     return (
 59:       <div style={{ color: COLORS.text, padding: 16, textAlign: 'center' }}>
 60:         No scored evaluations in period
 61:       </div>
 62:     );
 63:   }
 64: 
 65:   const yMin = Math.min(...allVals);
 66:   const yMax = Math.max(...allVals);
 67:   const yPad = (yMax - yMin) * 0.15 || 0.05;
 68: 
 69:   return (
 70:     <div aria-label={`Time series trend for ${metricName}`}>
 71:       <ResponsiveContainer width="100%" height={220}>
 72:         <ComposedChart data={chartData} margin={{ top: 8, right: 16, bottom: 4, left: 16 }}>
 73:           <CartesianGrid stroke={COLORS.grid} strokeDasharray="3 3" />
 74:           <XAxis
 75:             dataKey="time"
 76:             tick={{ fill: COLORS.text, fontSize: 11 }}
 77:             stroke={COLORS.grid}
 78:           />
 79:           <YAxis
 80:             domain={[yMin - yPad, yMax + yPad]}
 81:             tick={{ fill: COLORS.text, fontSize: 11 }}
 82:             stroke={COLORS.grid}
 83:             tickFormatter={(v: number) => v.toFixed(2)}
 84:             width={48}
 85:           />
 86:           <Tooltip
 87:             contentStyle={{
 88:               backgroundColor: COLORS.tooltip,
 89:               border: `1px solid ${COLORS.grid}`,
 90:               borderRadius: 6,
 91:               color: COLORS.text,
 92:               fontSize: 12,
 93:             }}
 94:             formatter={(v: number | string | undefined, name?: string) => [
 95:               typeof v === 'number' ? v.toFixed(4) : 'N/A',
 96:               name === 'avg' ? 'Average' : (name ?? '').toUpperCase(),
 97:             ]}
 98:             labelStyle={{ color: '#e6edf3' }}
 99:           />
100:           {/* P10-P90 band */}
101:           <Area
102:             type="monotone"
103:             dataKey="p90"
104:             stroke="none"
105:             fill={COLORS.band}
106:             connectNulls
107:           />
108:           <Area
109:             type="monotone"
110:             dataKey="p10"
111:             stroke="none"
112:             fill={COLORS.background}
113:             connectNulls
114:           />
115:           {/* P50 line */}
116:           <Line
117:             type="monotone"
118:             dataKey="p50"
119:             stroke={COLORS.bandStroke}
120:             strokeWidth={1}
121:             strokeDasharray="4 3"
122:             dot={false}
123:             connectNulls
124:           />
125:           {/* Average line */}
126:           <Line
127:             type="monotone"
128:             dataKey="avg"
129:             stroke={COLORS.line}
130:             strokeWidth={2}
131:             dot={{ fill: COLORS.line, r: 3 }}
132:             activeDot={{ r: 5 }}
133:             connectNulls
134:           />
135:         </ComposedChart>
136:       </ResponsiveContainer>
137:       <div style={{ display: 'flex', gap: 16, justifyContent: 'center', fontSize: 11, color: COLORS.text, marginTop: 4 }}>
138:         <span><span style={{ color: COLORS.line }}>&#9473;</span> avg</span>
139:         <span><span style={{ opacity: 0.5 }}>- -</span> p50</span>
140:         <span><span style={{ opacity: 0.3 }}>&#9608;</span> p10-p90</span>
141:       </div>
142:     </div>
143:   );
