37:     const pipeline = computePipelineView(evaluationsByMetric, dashboard);
38: 
39:     return c.json({ period, ...pipeline });
40:   } catch (err) {
41:     return c.json({ error: sanitizeErrorForResponse(err) }, HttpStatus.InternalServerError);
42:   }
