10: export function EvaluationEventOverlay({ evaluations, traceId }: EvaluationEventOverlayProps) {
11:   if (evaluations.length === 0) return null;
12: 
13:   const byName = new Map<string, EvaluationResult[]>();
14:   for (const ev of evaluations) {
15:     const name = ev.evaluationName;
16:     if (!byName.has(name)) byName.set(name, []);
17:     byName.get(name)!.push(ev);
18:   }
19: 
20:   return (
21:     <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8 }}>
22:       {[...byName.entries()].map(([name, evs]) => {
23:         const avg = evs.reduce((s, e) => s + (e.scoreValue ?? 0), 0) / evs.length;
24:         const card = (
25:           <div style={{
26:             display: 'flex', alignItems: 'center', gap: 6,
27:             padding: '4px 8px', borderRadius: 6,
28:             background: 'var(--bg-elevated)',
29:             border: '1px solid var(--border)',
30:             ...(traceId ? { cursor: 'pointer', transition: 'border-color 0.15s' } : {}),
31:           }}
32:           className={traceId ? 'eval-summary-card' : undefined}
33:           >
34:             <span style={{ fontSize: 12 }}>{name}</span>
35:             <ScoreBadge score={avg} metricName={name} label={avg.toFixed(2)} />
36:             <span style={{ fontSize: 10, color: 'var(--text-muted)' }}>({evs.length})</span>
37:           </div>
38:         );
39: 
40:         if (traceId) {
41:           return (
42:             <Link
43:               key={name}
44:               href={`/evaluations/trace/${traceId}?metric=${encodeURIComponent(name)}`}
45:               style={{ textDecoration: 'none', color: 'inherit' }}
46:             >
47:               {card}
48:             </Link>
49:           );
50:         }
51: 
52:         return <div key={name}>{card}</div>;
53:       })}
54:     </div>
55:   );
