 49: const labelSortFn: SortingFn<EvalRow> = (rowA, rowB) => {
 50:   const a = labelToOrdinal(rowA.original.label ?? 'unknown').ordinal;
 51:   const b = labelToOrdinal(rowB.original.label ?? 'unknown').ordinal;
 52:   return a - b;
 53: };
 54: 
 55: const categoryFilterFn: FilterFn<EvalRow> = (row, _id, filterValue: LabelFilterCategory[]) => {
 56:   if (!filterValue || filterValue.length === 0) return true;
 57:   const category = ordinalToCategory(labelToOrdinal(row.original.label ?? 'unknown').ordinal);
 58:   return filterValue.includes(category);
 59: };
