12: interface RoleContextValue {
13:   role: FeatureRoleType;
14:   config: RoleFeatureConfig;
15:   hasFeature: (feature: keyof RoleFeatureConfig) => boolean;
16: }
17: 
18: const RoleContext = createContext<RoleContextValue | null>(null);
19: 
20: export function RoleProvider({ children }: { children: ReactNode }) {
21:   const [, params] = useRoute('/role/:roleName');
22:   const roleName = params?.roleName as string | undefined;
23: 
24:   const role: FeatureRoleType =
25:     roleName && VALID_ROLES.has(roleName as FeatureRoleType)
26:       ? (roleName as FeatureRoleType)
27:       : DEFAULT_ROLE;
28: 
29:   useEffect(() => {
30:     if (roleName && !VALID_ROLES.has(roleName as FeatureRoleType)) {
31:       console.warn(`[RoleProvider] Unknown role "${roleName}", falling back to "executive". Valid roles: ${[...VALID_ROLES].join(', ')}`);
32:     }
33:   }, [roleName]);
34: 
35:   const value = useMemo<RoleContextValue>(() => {
36:     const config = getRoleFeatureConfig(role);
37:     return {
38:       role,
39:       config,
40:       hasFeature: (feature: keyof RoleFeatureConfig) => {
41:         const v = config[feature];
42:         if (typeof v === 'boolean') return v;
43:         return v !== undefined && v !== null;
44:       },
45:     };
46:   }, [role]);
47: 
48:   return (
49:     <RoleContext.Provider value={value}>
50:       {children}
51:     </RoleContext.Provider>
52:   );
53: }
