 24: /** Load spans for a session, defaulting to 30-day window. */
 25: async function loadSessionSpans(sessionId: string, startDate?: string, endDate?: string) {
 26:   const now = new Date();
 27:   const end = endDate ?? now.toISOString().split('T')[0];
 28:   const start = startDate ?? new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
 29:   const result = await queryTraces({
 30:     attributeFilter: { 'session.id': sessionId },
 31:     startDate: start,
 32:     endDate: end,
 33:     limit: 1000,
 34:   });
 35:   return result.traces;
 36: }
