11: export function SplitPane({ left, right, initialSplit = 50, minPct = 25, maxPct = 75 }: SplitPaneProps) {
12:   const [splitPct, setSplitPct] = useState(initialSplit);
13:   const containerRef = useRef<HTMLDivElement>(null);
14:   const dragging = useRef(false);
15: 
16:   const onMouseDown = useCallback(() => { dragging.current = true; }, []);
17: 
18:   useEffect(() => {
19:     function onMouseMove(e: MouseEvent) {
20:       if (!dragging.current || !containerRef.current) return;
21:       const rect = containerRef.current.getBoundingClientRect();
22:       const pct = ((e.clientX - rect.left) / rect.width) * 100;
23:       setSplitPct(Math.max(minPct, Math.min(maxPct, pct)));
24:     }
25:     function onMouseUp() { dragging.current = false; }
26:     document.addEventListener('mousemove', onMouseMove);
27:     document.addEventListener('mouseup', onMouseUp);
28:     return () => {
29:       document.removeEventListener('mousemove', onMouseMove);
30:       document.removeEventListener('mouseup', onMouseUp);
31:     };
32:   }, [minPct, maxPct]);
33: 
34:   return (
35:     <div ref={containerRef} style={{ display: 'flex', width: '100%', minHeight: 300 }}>
36:       <div style={{ width: `${splitPct}%`, overflow: 'auto' }}>{left}</div>
37:       <div
38:         role="separator"
39:         aria-orientation="vertical"
40:         aria-valuenow={Math.round(splitPct)}
41:         aria-valuemin={minPct}
42:         aria-valuemax={maxPct}
43:         aria-label="Resize panes"
44:         tabIndex={0}
45:         onMouseDown={onMouseDown}
46:         onKeyDown={(e) => {
47:           if (e.key === 'ArrowLeft') { e.preventDefault(); setSplitPct(p => Math.max(minPct, p - 2)); }
48:           if (e.key === 'ArrowRight') { e.preventDefault(); setSplitPct(p => Math.min(maxPct, p + 2)); }
49:         }}
50:         style={{
51:           width: 6,
52:           cursor: 'col-resize',
53:           background: 'var(--border)',
54:           flexShrink: 0,
55:           borderRadius: 3,
56:         }}
57:       />
58:       <div style={{ width: `${100 - splitPct}%`, overflow: 'auto' }}>{right}</div>
59:     </div>
60:   );
