  3: interface Shortcut {
  4:   key: string;
  5:   description: string;
  6:   scope: string;
  7:   action: () => void;
  8: }
  9: 
 10: interface KeyboardNavContextValue {
 11:   shortcuts: Shortcut[];
 12:   register: (key: string, description: string, scope: string, action: () => void) => () => void;
 13:   overlayOpen: boolean;
 14:   toggleOverlay: () => void;
 15: }
 16: 
 17: const KeyboardNavContext = createContext<KeyboardNavContextValue | null>(null);
 18: 
 19: export function useShortcut(key: string, description: string, scope: string, action: () => void) {
 20:   const ctx = useContext(KeyboardNavContext);
 21:   const register = ctx?.register;
 22:   const actionRef = useRef(action);
 23:   actionRef.current = action;
 24:   const stableAction = useCallback(() => actionRef.current(), []);
 25:   useEffect(() => {
 26:     if (!register) return;
 27:     return register(key, description, scope, stableAction);
 28:   }, [register, key, description, scope, stableAction]);
 29: }
 30: 
 31: export function useKeyboardNav() {
 32:   const ctx = useContext(KeyboardNavContext);
 33:   if (!ctx) throw new Error('useKeyboardNav must be used within KeyboardNavProvider');
 34:   return ctx;
 35: }
 36: 
 37: const IGNORED_TAGS = new Set(['INPUT', 'TEXTAREA', 'SELECT']);
 38: 
 39: export function KeyboardNavProvider({ children }: { children: ReactNode }) {
 40:   const shortcutsRef = useRef<Shortcut[]>([]);
 41:   const [shortcuts, setShortcuts] = useState<Shortcut[]>([]);
 42:   const [overlayOpen, setOverlayOpen] = useState(false);
 43:   const pendingRef = useRef<string | null>(null);
 44:   const pendingTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);
 45: 
 46:   const register = useCallback((key: string, description: string, scope: string, action: () => void) => {
 47:     const shortcut: Shortcut = { key, description, scope, action };
 48:     shortcutsRef.current = [...shortcutsRef.current, shortcut];
 49:     setShortcuts([...shortcutsRef.current]);
 50:     return () => {
 51:       shortcutsRef.current = shortcutsRef.current.filter(s => s !== shortcut);
 52:       setShortcuts([...shortcutsRef.current]);
 53:     };
 54:   }, []);
 55: 
 56:   const toggleOverlay = useCallback(() => setOverlayOpen(o => !o), []);
 57: 
 58:   useEffect(() => {
 59:     function handleKeydown(e: KeyboardEvent) {
 60:       if (IGNORED_TAGS.has((e.target as HTMLElement)?.tagName)) return;
 61:       if (e.ctrlKey || e.metaKey || e.altKey) return;
 62: 
 63:       const key = e.key;
 64: 
 65:       if (key === '?') {
 66:         e.preventDefault();
 67:         setOverlayOpen(o => !o);
 68:         return;
 69:       }
 70: 
 71:       if (key === 'Escape' && overlayOpen) {
 72:         e.preventDefault();
 73:         setOverlayOpen(false);
 74:         return;
 75:       }
 76: 
 77:       // Two-key combo support (e.g. "g h")
 78:       if (pendingRef.current) {
 79:         const combo = `${pendingRef.current} ${key}`;
 80:         pendingRef.current = null;
 81:         if (pendingTimerRef.current) clearTimeout(pendingTimerRef.current);
 82:         const match = shortcutsRef.current.find(s => s.key === combo);
 83:         if (match) {
 84:           e.preventDefault();
 85:           match.action();
 86:           return;
 87:         }
 88:       }
 89: 
 90:       // Check for single-key match
 91:       const single = shortcutsRef.current.find(s => s.key === key);
 92:       if (single) {
 93:         e.preventDefault();
 94:         single.action();
 95:         return;
 96:       }
 97: 
 98:       // Check if this could be the start of a combo
 99:       const hasCombo = shortcutsRef.current.some(s => s.key.startsWith(`${key} `));
100:       if (hasCombo) {
101:         pendingRef.current = key;
102:         pendingTimerRef.current = setTimeout(() => { pendingRef.current = null; }, 500);
103:       }
104:     }
105: 
106:     document.addEventListener('keydown', handleKeydown);
107:     return () => {
108:       document.removeEventListener('keydown', handleKeydown);
109:       if (pendingTimerRef.current) clearTimeout(pendingTimerRef.current);
110:     };
111:   }, [overlayOpen]);
112: 
113:   const value = useMemo<KeyboardNavContextValue>(
114:     () => ({ shortcuts, register, overlayOpen, toggleOverlay }),
115:     [shortcuts, register, overlayOpen, toggleOverlay],
116:   );
117: 
118:   return (
119:     <KeyboardNavContext.Provider value={value}>
120:       {children}
121:     </KeyboardNavContext.Provider>
122:   );
