 9: /** Filter out rule-based per-span evaluations; they have incompatible
10:  *  traceId granularity that inflates the coverage input universe.
11:  *  Keeps LLM judge evals (evaluatorType 'llm', undefined for seed/canary). */
12: function filterJudgeEvaluations(
13:   byMetric: Map<string, EvaluationResult[]>,
14: ): Map<string, EvaluationResult[]> {
15:   const filtered = new Map<string, EvaluationResult[]>();
16:   for (const [metric, evals] of byMetric) {
17:     const judgeEvals = evals.filter(e => e.evaluatorType !== 'rule');
18:     if (judgeEvals.length > 0) {
19:       filtered.set(metric, judgeEvals);
20:     }
21:   }
22:   return filtered;
23: }
