66: function buildEvalsBySpan(evaluations: Array<{ spanId?: string }>): Map<string, number> {
67:   const map = new Map<string, number>();
68:   for (const ev of evaluations) {
69:     if (ev.spanId) {
70:       map.set(ev.spanId, (map.get(ev.spanId) ?? 0) + 1);
71:     }
72:   }
73:   return map;
