38: function assertAgentStatsResponse(data: unknown): asserts data is AgentStatsResponse {
39:   if (!data || typeof data !== 'object') throw new Error(ErrorMessage.InvalidResponseShape);
40:   const obj = data as Record<string, unknown>;
41:   if (typeof obj.period !== 'string' || !Array.isArray(obj.agents)) {
42:     throw new Error(ErrorMessage.MissingPeriodOrAgents);
