 68: // -- scoreColorBand ---------------------------------------------------------
 69: 
 70: export function scoreColorBand(
 71:   value: number,
 72:   direction: ScoreDirection = 'maximize',
 73: ): ScoreColorBand {
 74:   const v = direction === 'minimize' ? 1 - value : value;
 75:   if (v >= 0.9) return 'excellent';
 76:   if (v >= 0.8) return 'good';
 77:   if (v >= 0.6) return 'adequate';
 78:   if (v >= 0.4) return 'poor';
 79:   return 'failing';
 80: }
 81: 
 82: // -- inferScoreDirection ----------------------------------------------------
 83: 
 84: type ThresholdDirection = 'above' | 'below';
 85: 
 86: export function inferScoreDirection(
 87:   alertDirection: ThresholdDirection | undefined,
 88: ): ScoreDirection {
 89:   return alertDirection === 'above' ? 'minimize' : 'maximize';
 90: }
