 92: // -- Label ordinal encoding -------------------------------------------------
 93: 
 94: const LABEL_ORDINAL_MAP: Record<string, { ordinal: number; category: LabelFilterCategory }> = {
 95:   excellent: { ordinal: 4, category: 'Pass' },
 96:   highly_relevant: { ordinal: 4, category: 'Pass' },
 97:   fully_faithful: { ordinal: 4, category: 'Pass' },
 98:   perfect: { ordinal: 4, category: 'Pass' },
 99:   relevant: { ordinal: 3, category: 'Pass' },
100:   good: { ordinal: 3, category: 'Pass' },
101:   faithful: { ordinal: 3, category: 'Pass' },
102:   pass: { ordinal: 3, category: 'Pass' },
103:   correct: { ordinal: 3, category: 'Pass' },
104:   coherent: { ordinal: 3, category: 'Pass' },
105:   partial: { ordinal: 2, category: 'Review' },
106:   borderline: { ordinal: 2, category: 'Review' },
107:   adequate: { ordinal: 2, category: 'Review' },
108:   mixed: { ordinal: 2, category: 'Review' },
109:   off_topic: { ordinal: 1, category: 'Fail' },
110:   irrelevant: { ordinal: 1, category: 'Fail' },
111:   unfaithful: { ordinal: 1, category: 'Fail' },
112:   fail: { ordinal: 1, category: 'Fail' },
113:   incorrect: { ordinal: 1, category: 'Fail' },
114:   incoherent: { ordinal: 1, category: 'Fail' },
115:   hallucinated: { ordinal: 0, category: 'Fail' },
116:   fabricated: { ordinal: 0, category: 'Fail' },
117:   toxic: { ordinal: 0, category: 'Fail' },
118:   dangerous: { ordinal: 0, category: 'Fail' },
119: };
120: 
121: function normalizeLabel(label: string): string {
122:   return label.toLowerCase().replace(/-/g, '_').trim();
123: }
124: 
125: export function labelToOrdinal(label: string): LabelOrdinal {
126:   const normalized = normalizeLabel(label);
127:   const entry = LABEL_ORDINAL_MAP[normalized];
128:   if (entry) {
129:     return { ordinal: entry.ordinal, category: entry.category, mapped: true };
130:   }
131:   return { ordinal: 2, category: 'Review', mapped: false };
132: }
133: 
134: export function ordinalToCategory(ordinal: number): LabelFilterCategory {
135:   if (ordinal >= 3) return 'Pass';
136:   if (ordinal === 2) return 'Review';
137:   return 'Fail';
138: }
