 37: function FreqBar({ label, count, max, color }: { label: string; count: number; max: number; color?: string }) {
 38:   const pct = max > 0 ? (count / max) * 100 : 0;
 39:   return (
 40:     <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 6 }}>
 41:       <div style={{
 42:         width: 160,
 43:         fontSize: 12,
 44:         fontFamily: 'var(--font-mono)',
 45:         color: 'var(--text-secondary)',
 46:         textAlign: 'right',
 47:         flexShrink: 0,
 48:         overflow: 'hidden',
 49:         textOverflow: 'ellipsis',
 50:         whiteSpace: 'nowrap',
 51:       }}>{label}</div>
 52:       <div style={{ flex: 1, background: 'var(--bg-elevated)', borderRadius: 2, height: 6, overflow: 'hidden' }}>
 53:         <div style={{
 54:           width: `${pct}%`,
 55:           height: '100%',
 56:           background: color ?? 'var(--accent)',
 57:           borderRadius: 2,
 58:           transition: 'width 0.4s ease',
 59:         }} />
 60:       </div>
 61:       <div style={{
 62:         fontFamily: 'var(--font-mono)',
 63:         fontSize: 11,
 64:         color: 'var(--text-muted)',
 65:         width: 36,
 66:         textAlign: 'right',
 67:         flexShrink: 0,
 68:       }}>{count}</div>
 69:     </div>
 70:   );
 71: }
