198:     projectName: 'unknown', workingDirectory: '', gitRepository: '', gitBranch: '',
199:     nodeVersion: '', resumeCount: 0, initialMessageCount: 0, initialContextTokens: 0,
200:     finalMessageCount: 0, taskCount: 0, uncommittedAtStart: 0,
201:   };
