216:     (e.evaluationName ?? '').toLowerCase().includes('hallucin') ||
217:     ((e.scoreLabel ?? '').toLowerCase() === 'fail' && typeof e.scoreValue === 'number' && e.scoreValue < HALLUCINATION_SCORE_THRESHOLD)
218:   );
219:   const failedEvals = evaluations.filter(e =>
220:     (e.scoreLabel ?? '').toLowerCase() === 'fail'
221:   );
222:   const errorCount = errorDetails.length;
223:   const hasIssues = alertSummary.totalFired > 0 || errorCount > 0 ||
224:     hallucinationEvals.length > 0 || failedEvals.length > 0;
225:   const issueHealth: SectionHealth = errorCount > 0 || hallucinationEvals.length > 0
226:     ? 'crit'
227:     : alertSummary.totalFired > 0 || failedEvals.length > 0
228:     ? 'warn'
229:     : 'ok';
