  2: import { computeMultiAgentEvaluation } from '../../../../dist/lib/quality-multi-agent.js';
  3: import { sanitizeErrorForResponse } from '../../../../dist/lib/error-sanitizer.js';
  4: import { loadTracesBySessionId, loadEvaluationsByTraceIds } from '../data-loader.js';
  5: import { queryTraces } from '../../../../dist/tools/query-traces.js';
  6: import type { StepScore } from '../../../../dist/backends/index.js';
  7: import { VALID_PERIODS, MAX_IDS, KNOWN_SOURCE_TYPES, HttpStatus } from '../../lib/constants.js';
  8: 
  9: export const agentRoutes = new Hono();
 10: 
 11: agentRoutes.get('/agents', async (c) => {
 12:   const periodParam = c.req.query('period') ?? '30d';
 13:   const periodDays = VALID_PERIODS[periodParam];
 14:   if (periodDays === undefined) {
 15:     return c.json({ error: `Invalid period value. Must be one of: ${Object.keys(VALID_PERIODS).join(', ')}` }, HttpStatus.BadRequest);
 16:   }
 17:   const now = new Date();
 18:   const endDate = now.toISOString().split('T')[0];
 19:   const startDate = new Date(now.getTime() - periodDays * 24 * 60 * 60 * 1000).toISOString().split('T')[0];
 20: 
 21:   try {
 22:     const result = await queryTraces({
 23:       attributeFilter: { 'hook.name': 'agent-post-tool' },
 24:       startDate,
 25:       endDate,
 26:       limit: 1000,
 27:     });
 28: 
 29:     // Build date bucket keys for the period (YYYY-MM-DD strings)
 30:     const dateBuckets: string[] = [];
 31:     for (let d = 0; d < periodDays; d++) {
 32:       const day = new Date(now.getTime() - (periodDays - 1 - d) * 24 * 60 * 60 * 1000);
 33:       dateBuckets.push(day.toISOString().split('T')[0]);
 34:     }
 35:     const bucketIndex = new Map(dateBuckets.map((b, i) => [b, i]));
 36: 
 37:     // Phase 1: aggregate agent stats from spans (prototype-safe accumulators)
 38:     const acc: Record<string, {
 39:       invocations: number;
 40:       errors: number;
 41:       rateLimitCount: number;
 42:       totalOutputSize: number;
 43:       sessions: Set<string>;
 44:       traceIds: Set<string>;
 45:       sourceTypes: Record<string, number>;
 46:       dailyCounts: number[];
 47:     }> = Object.create(null);
 48: 
 49:     // Build traceId -> agent names mapping for evaluation join
 50:     const traceToAgents = new Map<string, Set<string>>();
 51: 
 52:     for (const span of result.traces) {
 53:       const name = (span.attributes?.['gen_ai.agent.name'] as string | undefined) ?? 'unknown';
 54:       if (!acc[name]) {
 55:         acc[name] = { invocations: 0, errors: 0, rateLimitCount: 0, totalOutputSize: 0, sessions: new Set(), traceIds: new Set(), sourceTypes: Object.create(null), dailyCounts: new Array(periodDays).fill(0) };
 56:       }
 57:       acc[name].invocations++;
 58:       // Bucket into daily counts
 59:       if (span.startTimeUnixNano) {
 60:         const dayKey = new Date(span.startTimeUnixNano / 1_000_000).toISOString().split('T')[0];
 61:         const idx = bucketIndex.get(dayKey);
 62:         if (idx !== undefined) acc[name].dailyCounts[idx]++;
 63:       }
 64:       if (span.attributes?.['agent.has_error']) acc[name].errors++;
 65:       if (span.attributes?.['agent.has_rate_limit']) acc[name].rateLimitCount++;
 66:       acc[name].totalOutputSize += (span.attributes?.['agent.output_size'] as number | undefined) ?? 0;
 67:       const sid = span.attributes?.['session.id'] as string | undefined;
 68:       if (sid) acc[name].sessions.add(sid);
 69:       if (span.traceId) {
 70:         acc[name].traceIds.add(span.traceId);
 71:         if (!traceToAgents.has(span.traceId)) traceToAgents.set(span.traceId, new Set());
 72:         traceToAgents.get(span.traceId)!.add(name);
 73:       }
 74:       const rawSrc = (span.attributes?.['agent.source_type'] as string | undefined) ?? 'unknown';
 75:       const src = KNOWN_SOURCE_TYPES.has(rawSrc) ? rawSrc : 'other';
 76:       acc[name].sourceTypes[src] = (acc[name].sourceTypes[src] ?? 0) + 1;
 77:     }
 78: 
 79:     // Phase 2: load evaluations for all agent traceIds and join to agents
 80:     const allTraceIds = [...traceToAgents.keys()];
 81:     const evaluations = await loadEvaluationsByTraceIds(allTraceIds, startDate, endDate);
 82: 
 83:     // Accumulate per-agent evaluation scores by metric name (prototype-safe)
 84:     const agentEvalAcc: Record<string, Record<string, number[]>> = Object.create(null);
 85:     for (const ev of evaluations) {
 86:       if (!ev.traceId || ev.scoreValue == null || !Number.isFinite(ev.scoreValue)) continue;
 87:       const agentNames = traceToAgents.get(ev.traceId);
 88:       if (!agentNames) continue;
 89:       for (const agent of agentNames) {
 90:         if (!agentEvalAcc[agent]) agentEvalAcc[agent] = Object.create(null);
 91:         if (!agentEvalAcc[agent][ev.evaluationName]) agentEvalAcc[agent][ev.evaluationName] = [];
 92:         agentEvalAcc[agent][ev.evaluationName].push(ev.scoreValue);
 93:       }
 94:     }
 95: 
 96:     const agents = Object.entries(acc).map(([agentName, d]) => {
 97:       // Compute evaluation summary for this agent
 98:       const evalMetrics = agentEvalAcc[agentName] ?? {};
 99:       const evalSummary: Record<string, { avg: number; min: number; max: number; count: number }> = {};
100:       for (const [metric, scores] of Object.entries(evalMetrics)) {
101:         const sorted = [...scores].sort((a, b) => a - b);
102:         evalSummary[metric] = {
103:           avg: +(sorted.reduce((a, b) => a + b, 0) / sorted.length).toFixed(3),
104:           min: +sorted[0].toFixed(3),
105:           max: +sorted[sorted.length - 1].toFixed(3),
106:           count: sorted.length,
107:         };
108:       }
109: 
110:       const allSessionIds = [...d.sessions];
111:       const allTraceIdsList = [...d.traceIds];
112: 
113:       return {
114:         agentName,
115:         invocations: d.invocations,
116:         errors: d.errors,
117:         errorRate: d.invocations > 0 ? +(d.errors / d.invocations).toFixed(3) : 0,
118:         rateLimitCount: d.rateLimitCount,
119:         avgOutputSize: d.invocations > 0 ? Math.round(d.totalOutputSize / d.invocations) : 0,
120:         sessionCount: d.sessions.size,  // total unique sessions (invariant: >= sessionIds.length)
121:         sessionIds: allSessionIds.slice(0, MAX_IDS),
122:         sessionIdsTruncated: allSessionIds.length > MAX_IDS,
123:         traceIdsTotal: allTraceIdsList.length,
124:         traceIds: allTraceIdsList.slice(0, MAX_IDS),
125:         traceIdsTruncated: allTraceIdsList.length > MAX_IDS,
126:         sourceTypes: d.sourceTypes,
127:         dailyCounts: d.dailyCounts,
128:         evalSummary,
129:       };
130:     }).sort((a, b) => b.invocations - a.invocations);
131: 
132:     return c.json({ period: periodParam, startDate, endDate, agents });
133:   } catch (err) {
134:     return c.json({ error: sanitizeErrorForResponse(err) }, HttpStatus.InternalServerError);
135:   }
136: });
