138: /**
139:  * GET /api/agents/:sessionId
140:  * Loads spans for a session, builds agentMap, computes multi-agent evaluation.
141:  */
142: agentRoutes.get('/agents/:sessionId', async (c) => {
143:   const sessionId = c.req.param('sessionId');
144:   if (!sessionId) {
145:     return c.json({ error: 'sessionId is required' }, HttpStatus.BadRequest);
146:   }
147: 
