 13: function errorRateColor(rate: number): string {
 14:   if (rate === 0) return 'var(--status-healthy)';
 15:   if (rate < ERROR_RATE_WARNING_THRESHOLD) return 'var(--status-warning)';
 16:   return 'var(--status-critical)';
