358: /** Per-agent evaluation metrics (relevance, coherence, faithfulness, etc.) */
359: function EvalSummaryRow({ evalSummary }: { evalSummary: Record<string, EvalMetricSummary> }) {
360:   const metrics = Object.entries(evalSummary);
361:   if (metrics.length === 0) {
362:     return (
