447: /** Summary bar above the table -- totals at a glance */
448: export function AgentActivitySummary({ agents }: AgentActivityPanelProps) {
449:   const totalInvocations = agents.reduce((s, a) => s + a.invocations, 0);
450:   const totalErrors = agents.reduce((s, a) => s + a.errors, 0);
451:   const totalRateLimits = agents.reduce((s, a) => s + a.rateLimitCount, 0);
452:   const overallErrorRate = totalInvocations > 0 ? (totalErrors / totalInvocations) * 100 : 0;
453: 
454:   return (
455:     <div style={{
456:       display: 'flex',
457:       gap: 'var(--space-8)',
458:       padding: 'var(--space-4) var(--space-5)',
459:       background: 'var(--bg-elevated)',
460:       borderRadius: 'var(--radius)',
461:       border: '1px solid var(--border-subtle)',
462:       marginBottom: 'var(--space-5)',
463:       flexWrap: 'wrap',
464:     }}>
465:       {[
466:         { label: 'Agents', value: agents.length },
467:         { label: 'Invocations', value: totalInvocations.toLocaleString() },
468:         {
469:           label: 'Error Rate',
470:           value: totalInvocations > 0 ? `${overallErrorRate.toFixed(1)}%` : '\u2014',
471:           color: overallErrorRate === 0
472:             ? 'var(--status-healthy)'
473:             : overallErrorRate < ERROR_RATE_WARNING_THRESHOLD * 100
474:               ? 'var(--status-warning)'
475:               : 'var(--status-critical)',
476:         },
477:         {
478:           label: 'Rate Limits',
