 44: function DashboardPage({ period }: { period: Period }) {
 45:   const { data, isLoading, isFetching, error } = useDashboard(period);
 46: 
 47:   if (isLoading && !data) return <MetricGridSkeleton />;
 48:   if (error && !data) return <div className="error-state"><h2>Failed to load</h2><p>{error.message}</p></div>;
 49: 
 50:   if (!data || ('role' in data)) return <MetricGridSkeleton />;
 51:   const dashboard = data;
 52:   const sparklines = (data as QualityDashboardSummary & { sparklines?: Record<string, (number | null)[]> }).sparklines;
 53: 
 54:   if (dashboard.overallStatus === 'no_data') {
 55:     return (
 56:       <div className="empty-state">
 57:         <h2>No Evaluation Data</h2>
 58:         <p>No evaluations found for the selected period.</p>
 59:         <p style={{ marginTop: 8 }}>
 60:           Run evaluations using the <code>obs_inject_evaluations</code> tool to see metrics here.
 61:         </p>
 62:       </div>
 63:     );
 64:   }
 65: 
 66:   return (
 67:     <>
 68:       {isFetching && data && <div className="refetch-indicator">Updating...</div>}
 69:       <HealthOverview dashboard={dashboard} />
 70:       <MetricGrid metrics={dashboard.metrics} sparklines={sparklines} />
 71:       {dashboard.alerts.length > 0 && (
 72:         <div className="view-section">
 73:           <h3 className="section-heading">Active Alerts</h3>
 74:           <AlertList alerts={dashboard.alerts} />
 75:         </div>
 76:       )}
 77:       {dashboard.slaCompliance && dashboard.slaCompliance.length > 0 && (
 78:         <div className="view-section">
 79:           <h3 className="section-heading">SLA Compliance</h3>
 80:           <SLATable slas={dashboard.slaCompliance} />
 81:         </div>
 82:       )}
 83:     </>
 84:   );
 85: }
