 87: function RolePage({ role, period }: { role: RoleViewType; period: Period }) {
 88:   const { data, isLoading, error } = useDashboard(period, role);
 89: 
 90:   if (isLoading && !data) return <MetricGridSkeleton />;
 91:   if (error && !data) return <div className="error-state"><h2>Failed to load</h2><p>{error.message}</p></div>;
 92:   if (!data || !('role' in data)) return <MetricGridSkeleton />;
 93: 
 94:   switch (data.role) {
 95:     case 'executive':
 96:       return <ExecutiveView data={data} />;
 97:     case 'operator':
 98:       return <OperatorView data={data} />;
 99:     case 'auditor':
100:       return <AuditorView data={data} />;
101:     default:
102:       return null;
103:   }
104: }
