106: function MetricDetailPage({ name, period }: { name: string; period: Period }) {
107:   const { data, isLoading, error } = useMetricDetail(name, period);
108:   const { data: trendData } = useTrend(name, period, 10);
109: 
110:   if (isLoading) {
111:     return (
112:       <div>
113:         <Link href="/" className="back-link">&larr; Back to dashboard</Link>
114:         <div className="card skeleton" style={{ height: 300 }} />
115:       </div>
116:     );
117:   }
118:   if (error) {
119:     return (
120:       <div>
121:         <Link href="/" className="back-link">&larr; Back to dashboard</Link>
122:         <div className="error-state"><h2>Failed to load</h2><p>{error.message}</p></div>
123:       </div>
124:     );
125:   }
126: 
127:   if (!data) return null;
128:   const detail = data;
129: 
130:   return (
131:     <div>
132:       <Link href="/" className="back-link">&larr; Back to dashboard</Link>
133:       <div className="card" style={{ marginBottom: 24 }}>
134:         <div className="metric-card-header">
135:           <h2 className="page-heading">{detail.displayName}</h2>
136:           <StatusBadge status={detail.status} />
137:         </div>
138:         <div style={{ display: 'flex', gap: 32, marginTop: 12, flexWrap: 'wrap' }}>
139:           {(['avg', 'min', 'max', 'p50', 'p95', 'p99'] as const).map((key) => {
140:             const val = detail.values[key];
141:             return (
142:               <div key={key} style={{ textAlign: 'center' }}>
143:                 <div style={{ fontFamily: 'var(--font-mono)', fontSize: 20, fontWeight: 600 }}>
144:                   {val !== null && val !== undefined ? val.toFixed(4) : 'N/A'}
145:                 </div>
146:                 <div style={{ fontSize: 12, color: 'var(--text-secondary)', textTransform: 'uppercase' }}>{key}</div>
147:               </div>
148:             );
149:           })}
150:           <div style={{ textAlign: 'center' }}>
151:             <div style={{ fontFamily: 'var(--font-mono)', fontSize: 20, fontWeight: 600 }}>{detail.sampleCount}</div>
152:             <div style={{ fontSize: 12, color: 'var(--text-secondary)', textTransform: 'uppercase' }}>samples</div>
153:           </div>
154:         </div>
155:         <div style={{ display: 'flex', gap: 16, marginTop: 12, alignItems: 'center' }}>
156:           <TrendIndicator trend={detail.trend} />
157:           <ConfidenceBadge confidence={detail.confidence} />
158:         </div>
159:       </div>
160: 
161:       {detail.confidence && (
162:         <div className="view-section">
163:           <h3 className="section-heading">Confidence Analysis</h3>
164:           <div className="card">
165:             <ConfidencePanel confidence={detail.confidence} />
166:           </div>
167:         </div>
168:       )}
169: 
170:       <div className="view-section">
171:         <h3 className="section-heading">Trend</h3>
172:         <div className="card">
173:           <TrendChart
174:             trend={detail.trend}
175:             dynamics={(detail as MetricDetailResult & { dynamics?: MetricDynamics }).dynamics}
176:             warningThreshold={detail.alerts.find(a => a.severity === 'warning')?.threshold}
177:             criticalThreshold={detail.alerts.find(a => a.severity === 'critical')?.threshold}
178:             metricName={detail.displayName}
179:           />
180:         </div>
181:       </div>
182: 
183:       {trendData && trendData.trendData.length > 0 && (
184:         <div className="view-section">
185:           <h3 className="section-heading">
