239: function GlobalShortcuts({ setPeriod, navigate }: {
240:   setPeriod: (p: Period) => void;
241:   navigate: (path: string) => void;
242: }) {
243:   useShortcut('1', 'Switch to 24h', 'Period', useCallback(() => setPeriod('24h'), [setPeriod]));
244:   useShortcut('2', 'Switch to 7d', 'Period', useCallback(() => setPeriod('7d'), [setPeriod]));
245:   useShortcut('3', 'Switch to 30d', 'Period', useCallback(() => setPeriod('30d'), [setPeriod]));
246:   useShortcut('g h', 'Go to home', 'Navigation', useCallback(() => navigate('/'), [navigate]));
247:   useShortcut('g c', 'Go to correlations', 'Navigation', useCallback(() => navigate('/correlations'), [navigate]));
248:   useShortcut('g p', 'Go to pipeline', 'Navigation', useCallback(() => navigate('/pipeline'), [navigate]));
249:   useShortcut('g v', 'Go to coverage', 'Navigation', useCallback(() => navigate('/coverage'), [navigate]));
250:   useShortcut('g a', 'Go to agents', 'Navigation', useCallback(() => navigate('/agents'), [navigate]));
251:   return null;
252: }
